mapper.select:
/* sampleThumb.getList */
SELECT seq, filename, confirmed 
FROM sample_thumb
WHERE orderid = #{value}
ORDER BY seq DESC;

/* sampleThumb.getListByItem */
SELECT * 
FROM sample_thumb 
WHERE item = #{item}

AND seq < #{lastSeq}

AND confirmed = 1 
AND company = #{company}
ORDER BY seq DESC 
LIMIT #{pageSize};

/* sampleThumb.get */
SELECT *
FROM sample_thumb
WHERE seq = #{value}
